                  <Switch
                    checkedChildren="Simplified"
                    unCheckedChildren="Full Data"
                    defaultChecked={true}
                  />
                </Form.Item>
              </div>
            </TabPane>

            {/* Polling Tab */}
            <TabPane
              tab={
                <span className="flex items-center gap-2">
                  <SyncOutlined />
                  Polling
