interface FieldAssignment {
  id: string;
  name: string;
  type: 'stringValue' | 'numberValue' | 'booleanValue' | 'arrayValue' | 'objectValue';
  value: any;
}

interface TransformAssignmentCollectionProps {
  assignments: FieldAssignment[];
