                <Button
                  type="dashed"
                  size="small"
                  icon={<ClearOutlined />}
                  onClick={handleClearAll}
                  disabled={disabled}
                  danger
                >
                  Clear
                </Button>
              </Tooltip>
            )}
          </Space>
        </div>

        <Text type="secondary" style={{ fontSize: '12px' }}>
          Configure field assignments with type validation. Drag to reorder.
        </Text>
      </div>

      {/* Assignment Items with Drag & Drop */}
      <DndContext sensors={sensors} collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
        <SortableContext
          items={assignments.map((a) => a.id)}
          strategy={verticalListSortingStrategy}
        >
          <Space direction="vertical" style={{ width: '100%' }} size="small">
            {assignmentItems}
          </Space>
        </SortableContext>
      </DndContext>

      {/* Add Assignment Button */}
      <div style={{ marginTop: assignments.length > 0 ? '16px' : '0' }}>
        <Button
          type="dashed"
          onClick={handleAddAssignment}
          icon={<PlusOutlined />}
          style={{ width: '100%' }}
          disabled={disabled}
        >
          Add Assignment
        </Button>
      </div>

      {/* Empty State */}
      {assignments.length === 0 && (
        <div
          style={{
            textAlign: 'center',
            padding: '24px',
            color: '#8c8c8c',
            backgroundColor: '#fafafa',
            borderRadius: '6px',
            border: '1px dashed #d9d9d9',
            marginTop: '8px',
          }}
        >
          <Text type="secondary">No field assignments configured</Text>
          <br />
          <Text type="secondary" style={{ fontSize: '12px' }}>
            Click "Add Assignment" or "Add All" to get started
          </Text>
        </div>
      )}
    </div>
  );
};

