// 9. File Property Renderer
export const FileRenderer: React.FC<PropertyRendererProps> = ({
  property,
  value,
  onChange,
  disabled,
}) => {
  const handleUpload = (file: File) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      onChange({
        name: file.name,
        type: file.type,
        size: file.size,
        content: e.target?.result,
      });
    };
    reader.readAsDataURL(file);
    return false; // Prevent automatic upload
  };

  return (
    <div className="space-y-2">
      <label className="block text-sm font-medium text-gray-200">{property.displayName}</label>
      <Upload beforeUpload={handleUpload} maxCount={1} disabled={disabled} className="w-full">
