          <Input
            value={currentValue.value}
            onChange={(e) => handleValueChange(e.target.value)}
            placeholder={mode === 'id' ? 'Enter resource ID' : 'Enter resource URL'}
            disabled={disabled}
            className="flex-1 bg-gray-800 border-gray-600 text-gray-200"
          />
