            {...commonProps}
            rows={property.rows || property.typeOptions?.rows || 4}
            value={internalValue || property.default || ''}
            onChange={(e) => handleChange(e.target.value)}
          />
        );

      case 'number':
        return (
          <InputNumber
            {...commonProps}
            min={property.min}
            max={property.max}
            step={property.step}
            precision={property.typeOptions?.numberPrecision}
            value={internalValue || property.default || 0}
            onChange={handleChange}
          />
        );

      case 'boolean':
        return (
          <Switch
            disabled={disabled}
            checked={internalValue !== undefined ? internalValue : property.default}
            onChange={handleChange}
          />
        );

      case 'select':
      case 'options':
        return (
          <Select
            {...commonProps}
            value={internalValue || property.default}
            onChange={handleChange}
            showSearch
            filterOption={(input, option) =>
              (option?.children as unknown as string)?.toLowerCase().includes(input.toLowerCase())
            }
          >
            {property.options?.map((option) => (
              <Option key={String(option.value)} value={option.value}>
                {option.name}
              </Option>
            ))}
          </Select>
        );

      case 'multiSelect':
      case 'multiOptions':
        return (
          <Select
            {...commonProps}
            mode="multiple"
            value={internalValue || property.default || []}
            onChange={handleChange}
            showSearch
            filterOption={(input, option) =>
              (option?.children as unknown as string)?.toLowerCase().includes(input.toLowerCase())
            }
          >
            {property.options?.map((option) => (
              <Option key={String(option.value)} value={option.value}>
                {option.name}
              </Option>
            ))}
          </Select>
        );

      case 'dateTime':
        return (
          <DatePicker {...commonProps} showTime value={internalValue} onChange={handleChange} />
        );

      case 'color':
        return (
          <ColorPicker
            value={internalValue || property.default || '#1890ff'}
            onChange={(color) => handleChange(color.toHexString())}
            disabled={disabled}
            showText
          />
        );

      case 'file':
        return (
          <Upload
            disabled={disabled}
            beforeUpload={() => false} // Prevent auto upload
            onChange={(info) => handleChange(info.fileList)}
