    });

    return errors.size === 0;
  }, [properties, formState, validateProperty, onValidationChange]);

  // Validate on form state change
  React.useEffect(() => {
    validateAll();
  }, [validateAll]);

  const handlePropertyChange = useCallback(
    (name: string, value: any) => {
      onChange(name, value);
