                const newItem = {
                  ...itemValue,
                  [valueProperty.name]: newValue,
                };
                onChange([newItem]);
              }}
              context={context}
              disabled={isDisabled}
              theme={theme}
            />
          ))}
        </Card>
      );
    }

    // Multiple collection items
    return (
      <div>
        {collectionValue.map((item, index) => (
          <Card
            key={index}
            size="small"
            style={{
              marginBottom: 8,
              backgroundColor: themeStyles.backgroundColor,
              borderColor: themeStyles.borderColor,
              color: themeStyles.color,
