                      <div style={{ fontWeight: 500 }}>{displayName}</div>
                      <div style={{ fontSize: '11px', color: '#9ca3af' }}>
                        {credential.testedAt
                          ? credential.isValid
                            ? ' Tested'
                            : ' Test failed'
                          : 'Not tested'}
                        {credential.updatedAt && (
                          <span style={{ marginLeft: '8px' }}>
                            {new Date(credential.updatedAt).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                    </div>
                    <div style={{ display: 'flex', gap: '4px' }}>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          if (context.onEditCredential) {
                            context.onEditCredential(credential);
                          }
                        }}
                        style={{
                          background: 'none',
                          border: 'none',
                          color: '#6b7280',
                          cursor: 'pointer',
                          fontSize: '12px',
                          padding: '2px',
                        }}
                        onMouseEnter={(e) => (e.currentTarget.style.color = '#3b82f6')}
                        onMouseLeave={(e) => (e.currentTarget.style.color = '#6b7280')}
                        title="Edit credential"
                      >

                      </button>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          if (context.onDeleteCredential && confirm(`Delete "${displayName}"?`)) {
                            context.onDeleteCredential(credential);
                          }
                        }}
                        style={{
                          background: 'none',
                          border: 'none',
                          color: '#6b7280',
                          cursor: 'pointer',
                          fontSize: '12px',
                          padding: '2px',
                        }}
