                  <Tag color={getSeverityColor(selectedIncident.severity)}>
                    {selectedIncident.severity}
                  </Tag>
                </div>
              </div>
              <div>
                <Text className="text-gray-400">Status</Text>
                <div>
                  <Tag color={getStatusColor(selectedIncident.status)}>
