interface DashboardMetrics {
  organizations: {
    total: number;
    active: number;
    growth: number;
  };
  users: {
    total: number;
    active: number;
    newThisMonth: number;
    growth: number;
  };
  workflows: {
    total: number;
    active: number;
    executions: number;
    successRate: number;
  };
  security: {
    threats: number;
    alerts: number;
    complianceScore: number;
    lastIncident?: Date;
  };
  performance: {
    avgExecutionTime: number;
    systemUptime: number;
    errorRate: number;
    throughput: number;
  };
  costs: {
    monthlySpend: number;
    growth: number;
    costPerExecution: number;
  };
}

interface ActivityEvent {
  id: string;
  type: 'user_login' | 'workflow_execution' | 'security_alert' | 'system_event';
  title: string;
  description: string;
  timestamp: Date;
  severity: 'low' | 'medium' | 'high' | 'critical';
