      const mockAlerts: SecurityAlert[] = [
        {
          id: 'alert1',
          type: 'failed_login_attempts',
          severity: 'high',
          title: 'Multiple Failed Login Attempts',
          description: 'User john.doe@example.com has 5 failed login attempts in the last hour',
