    switch (severity) {
      case 'critical':
        return 'red';
      case 'high':
        return 'orange';
      case 'medium':
        return 'gold';
      case 'low':
        return 'green';
      default:
        return 'default';
    }
  };

  const getResultColor = (result: string) => {
    switch (result) {
      case 'success':
        return 'green';
      case 'failure':
        return 'red';
      case 'error':
        return 'orange';
      default:
        return 'default';
