            <span>
              <AlertOutlined /> Active Security Alerts
            </span>
          }
          style={{ marginBottom: 24 }}
        >
          {securityAlerts.map((alert) => (
            <Alert
              key={alert.id}
              message={alert.title}
              description={alert.description}
              type={
                alert.severity === 'critical'
                  ? 'error'
                  : alert.severity === 'high'
                    ? 'warning'
                    : 'info'
              }
              showIcon
              style={{ marginBottom: 8 }}
              action={
                <Button size="small" type="link">
                  Investigate
                </Button>
