public addReaction = asyncHandler(async (req: Request, res: Response): Promise<void> => {
    const { commentId } = req.params;
    const { type } = req.body;

    if (!commentId || !type) {
      res.status(400).json(ApiResponse.error('Comment ID and reaction type are required'));
      return;
    }

    const userId = (req as any).user?.id;
    if (!userId) {
      res.status(401).json(ApiResponse.error('Authentication required'));
      return;
    }

    const comment = await Comment.findById(commentId);
    if (!comment) {
      res.status(404).json(ApiResponse.error('Comment not found'));
      return;
    }

    // Check if user already reacted
    const existingReactionIndex = comment.reactions.findIndex((r) => r.userId === userId);

if (existingReactionIndex !== -1) {
      // Update existing reaction
      comment.reactions[existingReactionIndex].type = type;
      comment.reactions[existingReactionIndex].timestamp = new Date();
    } else {
      // Add new reaction
      comment.reactions.push({
        userId,
        type,
        timestamp: new Date(),
      });
    }

    await comment.save();

    res.json(
      ApiResponse.success({
        reactions: comment.reactions,
        message: 'Reaction added successfully',
      })
    );
  });

public removeReaction = asyncHandler(async (req: Request, res: Response): Promise<void> => {
    const { commentId } = req.params;

    if (!commentId) {
      res.status(400).json(ApiResponse.error('Comment ID is required'));
      return;
    }

    const userId = (req as any).user?.id;
    if (!userId) {
      res.status(401).json(ApiResponse.error('Authentication required'));
      return;
    }

    const comment = await Comment.findById(commentId);
    if (!comment) {
      res.status(404).json(ApiResponse.error('Comment not found'));
      return;
    }

    const reactionIndex = comment.reactions.findIndex((r) => r.userId === userId);
    if (reactionIndex === -1) {
      res.status(404).json(ApiResponse.error('User reaction not found'));
      return;
    }

    // Remove user's reaction
    comment.reactions.splice(reactionIndex, 1);
    await comment.save();

    res.json(
      ApiResponse.success({
        reactions: comment.reactions,
        message: 'Reaction removed successfully',
      })
    );
  });

