   */
  private matchMimeType(mime: string, pattern: string): boolean {
    if (pattern === '*/*' || pattern === mime) {
      return true;
    }

    if (pattern.endsWith('/*')) {
      const [typePattern] = pattern.split('/');
      const [type] = mime.split('/');
      return typePattern === type;
    }

    return false;
