  private revokeTokenFamily(tokenFamily: string): void {
    for (const [tokenId, data] of this.refreshTokenStore.entries()) {
      if (data.tokenFamily === tokenFamily) {
        this.refreshTokenStore.delete(tokenId);
      }
    }
  }

