  protected transformSort(sort: Sort<T> = {}): any {
    const transformed: Record<string, 1 | -1> = {};
    for (const [key, value] of Object.entries(sort)) {
      transformed[key] = value === 'desc' || value === -1 ? -1 : 1;
    }
    return transformed;
  }

