/**
 * Validate type of value
 */
function validateType(value: any, type?: string): string | null {
  if (!type) return null;

  switch (type) {
    case 'string':
      if (typeof value !== 'string') {
        return `Expected string, got ${typeof value}`;
      }
      break;

    case 'number':
      if (typeof value !== 'number' || Number.isNaN(value)) {
        return `Expected number, got ${typeof value}`;
      }
      break;

    case 'boolean':
      if (typeof value !== 'boolean') {
        return `Expected boolean, got ${typeof value}`;
      }
      break;

    case 'email':
      if (!validator.isEmail(value)) {
        return 'Invalid email address';
      }
      break;

    case 'url':
      if (!validator.isURL(value)) {
        return 'Invalid URL';
      }
      break;

    case 'uuid':
      if (!validator.isUUID(value)) {
        return 'Invalid UUID';
      }
      break;

    case 'json':
      try {
        JSON.parse(value);
      } catch {
        return 'Invalid JSON';
      }
      break;

    case 'array':
      if (!Array.isArray(value)) {
        return 'Expected array';
      }
      break;

    case 'object':
      if (typeof value !== 'object' || Array.isArray(value)) {
        return 'Expected object';
      }
      break;
  }

  return null;
}

/**
 * Validate constraints
 */
function validateConstraints(value: any, rule: ValidationRule): string[] {
  const errors: string[] = [];

  // Min/Max for numbers
  if (typeof value === 'number') {
    if (rule.min !== undefined && value < rule.min) {
      errors.push(`Value must be at least ${rule.min}`);
    }
    if (rule.max !== undefined && value > rule.max) {
      errors.push(`Value must be at most ${rule.max}`);
    }
  }

  // Length for strings and arrays
  if (typeof value === 'string' || Array.isArray(value)) {
    const length = value.length;
    if (rule.minLength !== undefined && length < rule.minLength) {
      errors.push(`Length must be at least ${rule.minLength}`);

}
    if (rule.maxLength !== undefined && length > rule.maxLength) {
      errors.push(`Length must be at most ${rule.maxLength}`);
    }
  }

  // Pattern matching
  if (rule.pattern && typeof value === 'string') {
    if (!rule.pattern.test(value)) {
      errors.push(`Value does not match required pattern`);
    }
  }

  // Enum validation
  if (rule.enum && !rule.enum.includes(value)) {
    errors.push(`Value must be one of: ${rule.enum.join(', ')}`);
  }

  // Custom validation
  if (rule.custom) {
    const result = rule.custom(value);
    if (typeof result === 'string') {
      errors.push(result);
    } else if (!result) {
      errors.push('Custom validation failed');
    }
  }

  return errors;
}

/**
 * Sanitize input based on rules
 */
function sanitizeInput(value: any, rule: ValidationRule): any {
  if (typeof value !== 'string') return value;

  let sanitized = value;

  // Basic sanitization
  if (rule.trim) {
    sanitized = sanitized.trim();
  }

  if (rule.toLowerCase) {
    sanitized = sanitized.toLowerCase();
  }

  if (rule.toUpperCase) {
    sanitized = sanitized.toUpperCase();
  }

  if (rule.escape) {
    sanitized = validator.escape(sanitized);
  }

  if (rule.normalizeEmail && rule.type === 'email') {
    sanitized = validator.normalizeEmail(sanitized) || sanitized;
  }

  // HTML sanitization
  sanitized = DOMPurify.sanitize(sanitized, {
    ALLOWED_TAGS: [],
    ALLOWED_ATTR: [],
  });

  return sanitized;
}

/**
