/**
 * Create secure file download middleware
 */
export function createSecureDownloadMiddleware(
  options: {
    basePath: string;
    allowedPaths?: string[];
    requireAuth?: boolean;
    logDownloads?: boolean;
  } = { basePath: '/uploads' }
) {
  return async (req: Request, res: Response, next: NextFunction): Promise<void> => {
    try {
      // Check authentication if required
      if (options.requireAuth && !(req as any).user) {
        res.status(401).json({
          success: false,
          error: {
            code: ERROR_CODES.UNAUTHORIZED,
            message: 'Authentication required',
          },
        });
        return;
      }

      // Get requested file path
      const filename = req.params.filename || req.query.filename;

      if (!filename || typeof filename !== 'string') {
        res.status(400).json({
          success: false,
          error: {
            code: ERROR_CODES.VALIDATION_ERROR,
            message: 'Filename is required',
          },
        });
        return;
      }

      // Prevent path traversal
      if (filename.includes('../') || filename.includes('..\\')) {
        res.status(400).json({
          success: false,
          error: {
            code: ERROR_CODES.SECURITY_VIOLATION,
            message: 'Invalid filename',
          },
        });
        return;
      }

const filePath = path.join(options.basePath, filename);

      // Verify file is within allowed paths
      const resolvedPath = path.resolve(filePath);
      const resolvedBase = path.resolve(options.basePath);

      if (!resolvedPath.startsWith(resolvedBase)) {
        res.status(403).json({
          success: false,
          error: {
            code: ERROR_CODES.FORBIDDEN,
            message: 'Access denied',
          },
        });
        return;
      }

      // Check if file exists
      try {
        await statAsync(resolvedPath);
      } catch (_error) {
        res.status(404).json({
          success: false,
          error: {
            code: ERROR_CODES.NOT_FOUND,
            message: 'File not found',
          },
        });
        return;
      }

      // Log download if enabled
      if (options.logDownloads && (global as any).auditLogger) {
        await (global as any).auditLogger.log({
          type: 'FILE_DOWNLOADED',
          severity: 'LOW',
          userId: (req as any).user?.id,
          action: 'File download',
          result: 'SUCCESS',
          details: { filename },
        });
      }

      // Set security headers
      res.setHeader('X-Content-Type-Options', 'nosniff');
      res.setHeader('Content-Security-Policy', "default-src 'none'");

      // Send file
      res.download(resolvedPath, path.basename(filename), (err) => {
        if (err) {
          if (!res.headersSent) {
            res.status(500).json({
              success: false,
              error: {
                code: ERROR_CODES.INTERNAL_ERROR,
                message: 'Failed to download file',
              },
            });
          }
        }
      });
    } catch (error) {
      next(error);
    }
  };

// Check role requirements
      if (roles.length > 0 && !roles.some((role) => user.roles?.includes(role))) {
        return res.status(403).json({
          success: false,
          error: {
            code: ERROR_CODES.FORBIDDEN,
            message: 'Insufficient role privileges',
          },
        });
      }

      // Check permission requirements
      if (permissions.length > 0 && !permissions.some((perm) => user.permissions?.includes(perm))) {
        return res.status(403).json({
          success: false,
          error: {
            code: ERROR_CODES.FORBIDDEN,
            message: 'Insufficient permissions',
          },
        });
      }

      // Attach user to request
      req.user = user;
      req.token = token;
      req.sessionId = user.sessionId;

      next();
    } catch (error: any) {
      if (error.message === 'Token has expired') {
        return res.status(401).json({
          success: false,
          error: {
            code: ERROR_CODES.TOKEN_EXPIRED,
            message: 'Access token has expired',
          },
        });
      } else if (error.message === 'Token has been revoked') {
        return res.status(401).json({
          success: false,
          error: {
            code: ERROR_CODES.TOKEN_REVOKED,
            message: 'Access token has been revoked',
          },
        });
      }

      return res.status(401).json({
        success: false,
        error: {
          code: ERROR_CODES.UNAUTHORIZED,
          message: 'Invalid authentication token',
        },
      });
    }
  };

