   */

: void {
    if (this.heartbeatInterval) {
      return;
    }

    this.heartbeatInterval = setInterval(async () => {
      try {
        await this.onHeartbeat();
        this.emit('heartbeat', {
          name: this.config.name,
          status: this.state.status,
        });
      } catch (error: any) {
        this.handleError(error);
      }
    }, 60000); // Every minute
  }

