protected async makeRequest(endpoint: string, options: RequestOptions = {}): Promise<any> {
    // Check rate limits
    await this.checkRateLimit();

    // Build full URL
    const url = this.buildUrl(endpoint, options.queryParams);

    // Add authentication headers
    const headers = await this.getAuthHeaders(options.headers);

    // Make the request with retries
    const response = await this.executeWithRetry(
      async () => {
        return this.performRequest(url, {
          ...options,
          headers,
        });
      },
      options.retryCount || 3,
      options.retryDelay || 1000
    );

    // Update rate limit counters
    this.incrementRequestCount();

    return response;
  }

  /**
   * Build full URL with query parameters
   */

private buildUrl(endpoint: string, queryParams?: Record<string, any>): string {
    const baseUrl = this.config.baseUrl || '';
    let url = endpoint.startsWith('http') ? endpoint : `${baseUrl}${endpoint}`;

    if (queryParams) {
      const params = new URLSearchParams();
      Object.entries(queryParams).forEach(([key, value]) => {
        if (value !== undefined && value !== null) {
          params.append(key, String(value));
        }
      });
      const queryString = params.toString();
      if (queryString) {
        url += `${url.includes('?') ? '&' : '?'}${queryString}`;
      }
    }

    return url;
  }

  /**
   * Get authentication headers based on auth type
   */

getAuthHeaders(
    additionalHeaders?: Record<string, string>
  ): Promise<Record<string, string>> {
    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
      ...additionalHeaders,
    };

    if (!this.credentials) {
      return headers;
    }

    switch (this.config.authType) {
      case 'apiKey':
        headers.Authorization = `Bearer ${this.credentials.data.apiKey}`;
        break;

      case 'oauth2':
        // Check if token needs refresh
        if (this.credentials.expiresAt && new Date() >= this.credentials.expiresAt) {
          await this.refreshOAuth2Token();
        }
        headers.Authorization = `Bearer ${this.credentials.data.accessToken}`;
        break;

      case 'basic': {
        const auth = Buffer.from(
          `${this.credentials.data.username}:${this.credentials.data.password}`
        ).toString('base64');
        headers.Authorization = `Basic ${auth}`;
        break;
      }

performRequest(url: string, options: RequestOptions): Promise<any> {
    const controller = new AbortController();
    const timeout = options.timeout || 30000;

    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
      const response = await fetch(url, {
        method: options.method || 'GET',
        headers: options.headers,
        body: options.body ? JSON.stringify(options.body) : undefined,
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw await this.handleResponseError(response);
      }

      const contentType = response.headers.get('content-type');
      if (contentType?.includes('application/json')) {
        return await response.json();
      }

      return await response.text();
    } catch (error: any) {
      clearTimeout(timeoutId);

      if (error.name === 'AbortError') {
        throw this.createError('TIMEOUT', `Request timed out after ${timeout}ms`);
      }

      throw error;
    }
  }

  /**
   * Handle HTTP response errors
   */

handleResponseError(response: Response): Promise<IntegrationError> {
    let errorBody: any;

    try {
      errorBody = await response.json();
    } catch {
      errorBody = await response.text();
    }

    const error = this.createError(
      `HTTP_${response.status}`,
      errorBody?.message || `Request failed with status ${response.status}`,
      response.status,
      errorBody
    );

    // Determine if error is retryable
    error.retryable = [408, 429, 500, 502, 503, 504].includes(response.status);

    return error;
  }

  /**
   * Execute function with retry logic
   */

private async executeWithRetry<T>(
    fn: () => Promise<T>,
    maxRetries: number = 3,
    delay: number = 1000
  ): Promise<T> {
    let lastError: any;

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        return await fn();
      } catch (error:

): Promise<T> {
    let lastError: any;

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        return await fn();
      } catch (error: any) {
        lastError = error;

        if (!error.retryable || attempt === maxRetries) {
          throw error;
        }

        // Exponential backoff
        const waitTime = delay * 2 ** attempt;
        await this.sleep(waitTime);
      }
    }

    throw lastError;
  }

  /**
   * Check rate limits
   */

: Promise<void> {
    if (!this.config.rateLimit) {
      return;
    }

    const now = Date.now();
    const periodMs = this.config.rateLimit.period * 1000;

    // Reset counter if period has passed
    if (now - this.requestResetTime > periodMs) {
      this.requestCount = 0;
      this.requestResetTime = now;
    }

    // Check if limit exceeded
    if (this.requestCount >= this.config.rateLimit.requests) {
      const waitTime = periodMs - (now - this.requestResetTime);
      throw this.createError(
        'RATE_LIMIT_EXCEEDED',
        `Rate limit exceeded. Try again in ${Math.ceil(waitTime / 1000)} seconds`,
        429
      );
    }
  }

  /**
   * Increment request counter
   */

protected createError(
    code: string,
    message: string,
    statusCode?: number,
    details?: any
  ): IntegrationError {
    const error = new Error(message) as IntegrationError;
    error.code = code;
    error.statusCode = statusCode;
    error.details = details;
    error.name = 'IntegrationError';
    return error;
  }

