  /**
   * Exchange authorization code for tokens
   */
  async exchangeCodeForToken(
    code: string,
    state: string,
    integrationName: string,
    userId: string
  ): Promise<OAuth2Token> {
    // Verify state
    const pendingAuth = this.pendingAuthorizations.get(state);
    if (!pendingAuth) {
      throw new Error('Invalid or expired state parameter');
    }

    try {
      const params: Record<string, string> = {
        grant_type: 'authorization_code',
        code,
        client_id: this.config.clientId,
        client_secret: this.config.clientSecret,
        redirect_uri: this.config.redirectUri,
      };

      // Add PKCE verifier if used
      if (this.config.usePKCE && pendingAuth.codeVerifier) {
        params.code_verifier = pendingAuth.codeVerifier;
      }

      // Make token request
      const response = await this.httpClient.post(
        this.config.tokenUrl,
        new URLSearchParams(params),
        {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        }
      );

      const tokenData = response.data;

      // Create token object
      const token: OAuth2Token = {
        accessToken: tokenData.access_token,
        refreshToken: tokenData.refresh_token,
        tokenType: tokenData.token_type || 'Bearer',
        expiresIn: tokenData.expires_in,
        expiresAt: tokenData.expires_in
          ? new Date(Date.now() + tokenData.expires_in * 1000)
          : undefined,
        scope: tokenData.scope,
        idToken: tokenData.id_token,

// Create and store session
      const sessionId = this.generateSessionId();
      const session: OAuth2Session = {
        id: sessionId,
        integrationName,
        userId,
        token,
        createdAt: new Date(),
        updatedAt: new Date(),
        state,
        codeVerifier: pendingAuth.codeVerifier,
        codeChallenge: pendingAuth.codeChallenge,
        nonce: pendingAuth.nonce,
      };

      this.sessions.set(sessionId, session);

      // Clean up pending authorization
      this.pendingAuthorizations.delete(state);

      this.emit('token:obtained', {
        sessionId,
        integrationName,
        userId,
      });

      return token;
    } catch (error: any) {
      this.emit('token:error', {
        integrationName,
        userId,
        error: error.message,
      });

      throw new Error(`Failed to exchange code for token: ${error.message}`);
    }
  }

