  DeleteOutlined,
  EditOutlined,
  MoreOutlined,
  PlayCircleOutlined,
  ReloadOutlined,
  SettingOutlined,
  StopOutlined,
} from '@ant-design/icons';
import { Dropdown, Menu, Tooltip } from 'antd';
import React, { useRef, useState } from 'react';
import { useNodeTheme } from '../themes'

;

interface EnhancedNodeToolbarProps {
  nodeId: string;
  visible: boolean;
  actions?: ToolbarAction[];
  theme?: NodeTheme;
  onActionClick: (actionId: string, nodeId: string) => void;
  position?: 'top' | 'bottom' | 'left' | 'right';
  size?: 'small' | 'medium' | 'large';
  variant?: 'default' | 'compact' | 'floating';
}

const EnhancedNodeToolbar: React.FC<EnhancedNodeToolbarProps> = ({
  nodeId,
  visible,
  actions = [],
  theme: propTheme,
  onActionClick,
  position = 'top',
  size = 'medium',
  variant = 'default',
}) => {
  const { theme: contextTheme } = useNodeTheme();
  const theme = propTheme || contextTheme;
  const [hoveredAction, setHoveredAction] = useState<string | null>(null);
  const toolbarRef = useRef<HTMLDivElement>(null);

  // Default actions if none provided
  const defaultActions: ToolbarAction[] = [
    {
      id: 'play',
      icon: 'PlayCircleOutlined',
      label: 'Run Node',
      action: 'execute',
      shortcut: 'Ctrl+R',
    },
    {
      id: 'stop',
      icon: 'StopOutlined',
      label: 'Stop Execution',
      action: 'stop',
    },
    {
      id: 'copy',
      icon: 'CopyOutlined',
      label: 'Duplicate Node',
      action: 'duplicate',
      shortcut: 'Ctrl+D',
    },
    {
      id: 'edit',
      icon: 'EditOutlined',
      label: 'Edit Node',
      action: 'edit',
      shortcut: 'Enter',
    },
    {
      id: 'delete',
      icon: 'DeleteOutlined',
      label: 'Delete Node',
      action: 'delete',
      shortcut: 'Del',
    },
    {
      id: 'more',
      icon: 'MoreOutlined',
      label: 'More Actions',
      action: 'menu',
    },
  ];

  const finalActions = actions.length > 0 ? actions : defaultActions;

  const getPositionStyles = (): React.CSSProperties => {
    const offset = variant === 'floating' ? 12 : 8;
    const baseStyles: React.CSSProperties = {
      position: 'absolute',
      zIndex: 9999,
    };

    switch (position) {
      case 'top':
        return {
          ...baseStyles,
          top: `-${offset + 32}px`,
          left: '50%',
          transform: 'translateX(-50%)',
        };
      case 'bottom':
        return {
          ...baseStyles,
