                      </Space>
                    }
                    description={
                      <Space direction="vertical" size="small">
                        <div>
                          <strong>Implementation:</strong> {rec.implementation}
                        </div>
                        <div>
                          <strong>Impact:</strong> {rec.estimatedImpact}
                        </div>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        )}
      </Space>
    );
  };

  const getExecutionStatusColor = (status: string) => {
    switch (status) {
      case 'running':
        return 'blue';
      case 'completed':
        return 'green';
      case 'failed':
        return 'red';
      case 'cancelled':
        return 'orange';
      case 'skipped':
        return 'default';
      default:
        return 'default';
    }
  };

  return (
    <Modal
      title={
        <Space>
          <ClockCircleOutlined />
          Workflow Scheduling
        </Space>
      }
      open={visible}
      onCancel={onClose}
      width={1000}
      footer={null}
      className={cn('workflow-scheduling-panel')}
    >
      <Tabs activeKey={activeTab} onChange={setActiveTab}>
        <TabPane
          tab={
            <span>
              <CalendarOutlined />
              Schedules ({schedules.length})
            </span>
          }
          key="schedules"
        >
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card title="Create Schedule" size="small">
              {renderScheduleForm()}
            </Card>
            <Card title="Active Schedules" size="small">
              {renderSchedulesList()}
            </Card>
          </div>
        </TabPane>

        <TabPane
          tab={
            <span>
              <ThunderboltOutlined />
              Executions ({activeExecutions.length})
            </span>
          }
          key="executions"
        >
          {renderExecutionsList()}
        </TabPane>

        <TabPane
          tab={
            <span>
              <BarChartOutlined />
              Analytics
            </span>
          }
          key="analytics"
        >
          <div className="mb-4">
            <Select
              placeholder="Select a schedule to view analytics"
              value={selectedSchedule?.id}
              onChange={(scheduleId) => {
                const schedule = schedules.find((s) => s.id === scheduleId);
                setSelectedSchedule(schedule || null);
              }}
              style={{ width: 300 }}
            >
              {schedules.map((schedule) => (
                <Option key={schedule.id} value={schedule.id}>
                  {schedule.name}
                </Option>
              ))}
            </Select>
          </div>
          {renderAnalytics()}
        </TabPane>
      </Tabs>
    </Modal>
  );
};

