            disabled={disabled}
            className="w-full"
            style={{ backgroundColor: theme === 'dark' ? '#1f2937' : undefined }}
          >
            {property.options?.map((option: any) => (
              <Option key={option.value} value={option.value}>
                {option.label}
              </Option>
            ))}
          </Select>
        );

      case 'multiSelect':
        return (
          <Select
            mode="multiple"
            value={value || []}
            onChange={onChange}
            placeholder={property.placeholder}
            disabled={disabled}
            className="w-full"
            style={{ backgroundColor: theme === 'dark' ? '#1f2937' : undefined }}
          >
            {property.options?.map((option: any) => (
              <Option key={option.value} value={option.value}>
                {option.label}
              </Option>
            ))}
          </Select>
        );

      case 'dateTime':
        return (
          <DatePicker
            value={value}
            onChange={onChange}
            placeholder={property.placeholder}
            disabled={disabled}
            showTime
            className="w-full"
            style={{ backgroundColor: theme === 'dark' ? '#1f2937' : undefined }}
          />
        );

      case 'color':
        return (
          <ColorPicker
            value={value}
            onChange={(color) => onChange(color.toHexString())}
            disabled={disabled}
          />
        );

      case 'file':
        return (
          <Upload
            beforeUpload={() => false}
            onChange={(info) => {
              if (info.file) {
                onChange(info.file);
              }
            }}
            disabled={disabled}
          >
            <Button icon={<PlusOutlined />} disabled={disabled}>
              Upload File
            </Button>
          </Upload>
        );

      case 'json':
        return (
          <div>
            <TextArea
              value={typeof value === 'string' ? value : JSON.stringify(value, null, 2)}
              onChange={(e) => {
                try {
                  const parsed = JSON.parse(e.target.value);
                  onChange(parsed);
                } catch {
                  onChange(e.target.value);
                }
              }}
              placeholder={property.placeholder}
              disabled={disabled}
              rows={6}
              className={cn(
                'font-mono text-xs',
                theme === 'dark' ? 'bg-gray-800 border-gray-600' : ''
              )}
            />
            {value && typeof value === 'object' && (
              <div className="mt-2">
                <Button size="small" onClick={() => setIsExpanded(!isExpanded)} type="text">
                  {isExpanded ? 'Collapse' : 'Expand'} JSON
                </Button>
              </div>
            )}
          </div>
        );

      case 'expression':
        return (
          <div>
            <TextArea
              value={value || ''}
              onChange={(e) => onChange(e.target.value)}
              placeholder={property.placeholder || 'Enter JavaScript expression...'}
              disabled={disabled}
              rows={3}
              className={cn(
                'font-mono text-xs',
                theme === 'dark' ? 'bg-gray-800 border-gray-600' : ''
              )}
            />
            <div className="text-xs text-gray-500 mt-1">
              Use $input to reference input data, e.g., $input.user.name
            </div>
          </div>
        );

      case 'collection':
        return (
          <div className="space-y-2">
            {(value || []).map((item: any, index: number) => (
              <Card
                key={index}
                size="small"
                className={theme === 'dark' ? 'bg-gray-800 border-gray-600' : ''}
                title={`Item ${index + 1}`}
                extra={
                  <Button
                    type="text"
                    danger
                    size="small"
                    icon={<DeleteOutlined />}
                    onClick={() => {
                      const newValue = [...(value || [])];
                      newValue.splice(index, 1);
                      onChange(newValue);
                    }}
                    disabled={disabled}
                  />
                }
              >
                <div className="space-y-2">
                  {property.collectionSchema?.map((field: any) => (
                    <div key={field.name}>
                      <label className="text-sm font-medium text-gray-300">
                        {field.label || field.name}
                      </label>
                      <PropertyField
                        property={field}
                        value={item[field.name]}
                        onChange={(fieldValue) => {
                          const newValue = [...(value || [])];
                          newValue[index] = { ...newValue[index], [field.name]: fieldValue };
                          onChange(newValue);
                        }}
                        disabled={disabled}
                        theme={theme}
                      />
                    </div>
                  ))}
                </div>
              </Card>
            ))}
            <Button
              type="dashed"
              onClick={() => {
                const newValue = [...(value || []), {}];
                onChange(newValue);
              }}
              disabled={disabled}
              className="w-full"
            >
              <PlusOutlined /> Add Item
            </Button>
          </div>
        );

      case 'credentialsSelect':
        return (
          <div className="space-y-2">
            <Select
              value={value?.credentialId}
              onChange={(credentialId) => onChange({ ...value, credentialId })}
              placeholder="Select credential"
              disabled={disabled}
              className="w-full"
              style={{ backgroundColor: theme === 'dark' ? '#1f2937' : undefined }}
            >
              {/* This would be populated with available credentials */}
