                const newItem = {
                  ...itemValue,
                  [valueProperty.name]: newValue,
                };
                onChange([newItem]);
              }}
              context={context}
              disabled={isDisabled}
              theme={theme}
            />
          ))}
        </Card>
      );
    }

    // Multiple collection items
