      color: '#1f2937',
      dropdownStyle: {
        backgroundColor: '#ffffff',
        border: '1px solid #d1d5db',
      },
    };
  } else {
    return {
      backgroundColor: '#4b5563',
      borderColor: hasErrors ? '#ff4d4f' : '#6b7280',
      color: '#ffffff',
      dropdownStyle: {
        backgroundColor: '#374151',
        border: '1px solid #6b7280',
      },
    };
  }
};

