      >
        <Table
          dataSource={filteredEvents}
          columns={columns}
          rowKey="id"
          loading={loading}
          pagination={{
            pageSize: 50,
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total, range) => `${range[0]}-${range[1]} of ${total} events`,
          }}
          scroll={{ x: 1200 }}
        />
      </Card>

      {/* Event Details Modal */}
      <Modal
        title="Event Details"
        open={!!selectedEvent}
        onCancel={() => setSelectedEvent(null)}
        footer={null}
        width={800}
      >
