          style={{ marginBottom: 24 }}
        >
          {securityAlerts.map((alert) => (
            <Alert
              key={alert.id}
              message={alert.title}
              description={alert.description}
              type={
                alert.severity === 'critical'
                  ? 'error'
                  : alert.severity === 'high'
                    ? 'warning'
                    : 'info'
              }
              showIcon
              style={{ marginBottom: 8 }}
              action={
                <Button size="small" type="link">
                  Investigate
                </Button>
              }
            />
          ))}
        </Card>
      )}

      {/* Filters */}
      <Card
        title={
          <span>
            <FilterOutlined /> Filters & Search
          </span>
        }
        style={{ marginBottom: 24 }}
        extra={
          <Space>
            <Button icon={<ReloadOutlined />} onClick={loadAuditData} loading={loading}>
              Refresh
            </Button>
            <Button icon={<ExportOutlined />} onClick={() => setExportModalVisible(true)}>
              Export
            </Button>
            <Button icon={<CheckCircleOutlined />} onClick={generateComplianceReport}>
              Generate Report
            </Button>
          </Space>
        }
      >
        <Space wrap style={{ marginBottom: 16 }}>
          <RangePicker
            value={dateRange}
            onChange={setDateRange}
            showTime
            format="YYYY-MM-DD HH:mm"
            placeholder={['Start Date', 'End Date']}
          />

          <Select
            placeholder="Select User"
            value={selectedUser}
            onChange={setSelectedUser}
            allowClear
            style={{ width: 150 }}
          >
