            </Button>
            <Button icon={<DownloadOutlined />} onClick={() => exportData('csv')}>
              Export as CSV
            </Button>
          </Space>
          <Text type="secondary">
            Export will include all events within the selected date range.
          </Text>
        </Space>
      </Modal>
    </div>
  );
};

